exports.handler = function(requestor, args, request) {
  return requestor.role === 'ADMIN';
}

exports.handler = function(requestor, args, request) {
  return requestor.role === 'ADMIN' || requestor.role === "MEMBER";
}

// shorter cache duration because remote data can change
exports.handler = async function(requestor, args, request) {
  // for non-trivial use-cases
  return request(api_endpoint, `
    allClients {
      id
    }
  `).then(data => {
    // do something with data
    return true;
  }).catch(err => {
    // error occured
    return false;
  })
}
